catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Get the g_form object
    var g_form = control;

    // Get the Separation Date value
    var separationDate = g_form.getValue('separation_date');

    // Get tomorrow's date
    var tomorrow = new GlideDateTime();
    tomorrow.addDays(1);

    // Check if the Separation Date is not in the future
    if (new GlideDateTime(separationDate).compareTo(tomorrow) < 0) {
        // Display a message
        g_form.addErrorMessage('Separation Date should be in the future.');

        // Clear the Separation Date value
        g_form.setValue('separation_date', '');
    } else {
        // Clear any previous error messages
        g_form.clearMessages();
    }
}
