catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Get the g_form object
    var g_form = control;

    // Get the value of the Separation Type field
    var separationType = g_form.getValue('separation_type');

    // Get the reference to the Priority field
    var priorityField = g_form.getControl('priority');

    // Check if Immediate Termination is selected
    if (separationType === 'immediate_termination') {
        // Set the Priority to Critical
        g_form.setValue('priority', '1'); // Assuming '1' represents 'Critical'
    } else {
        // Reset the Priority to its default value or perform any other necessary actions
        priorityField.setDefaultValue();
    }
}
