catalog_script_client.script:
function onChangeRequestedFor() {
    // Get the selected user from the "Requested for" field
    var requestedForUser = g_form.getValue('requested_for');

    // Use GlideAjax or any other method to fetch additional information based on the selected user
    // For demonstration purposes, let's assume there is a function getAdditionalUserInfo(userId) that returns user information

    var additionalUserInfo = getAdditionalUserInfo(requestedForUser);

    // Populate the Location field
    g_form.setValue('location', additionalUserInfo.location);

    // Populate the Contact Number field
    g_form.setValue('contact_number', additionalUserInfo.contactNumber);

    // Populate the Email field
    g_form.setValue('email', additionalUserInfo.email);
}

// Attach the onChange function to the "Requested for" field
g_form.addOnChange('requested_for', onChangeRequestedFor);
