sys_script.script:
(function() {
    // Attach the client script to the onChange event of the catalog item field
    g_form.addOnChange('catalog_item', function() {
        // Get the selected catalog item
        var catalogItem = g_form.getValue('catalog_item');

        // Define mapping of catalog items to sources
        var sourceMap = {
            'Hardware Request': 'Hardware Catalog',
            'Software Installation': 'Software Catalog',
            'Network Setup': 'Network Catalog',
            // Add more mappings as needed
        };

        // If a source is mapped for the selected catalog item, populate the Source field
        if (sourceMap.hasOwnProperty(catalogItem)) {
            g_form.setValue('source', sourceMap[catalogItem]);
        } else {
            // If no mapping is found, you can provide a default value or leave the field empty
            // g_form.setValue('source', ''); // Leave the field empty
            // g_form.setValue('source', 'Default Source'); // Provide a default value
        }
    });
})();
